import random
import math

#To generate random prime less than N
def randPrime(N):
	primes = []
	for q in range(2,N+1):
		if(isPrime(q)):
			primes.append(q)
	return primes[random.randint(0,len(primes)-1)]

# To check if a number is prime
def isPrime(q):
	if(q > 1):
		for i in range(2, int(math.sqrt(q)) + 1):
			if (q % i == 0):
				return False
		return True
	else:
		return False

#pattern matching
def randPatternMatch(eps,p,x):
	N = findN(eps,len(p))
	q = randPrime(N) 
	print(q)
	return modPatternMatch(q,p,x)

#pattern matching with wildcard
def randPatternMatchWildcard(eps,p,x):
	N = findN(eps,len(p))
	q = randPrime(N)
	return modPatternMatchWildcard(q,p,x)

# return appropriate N that satisfies the error bounds
def findN(eps,m):
	k = (m/eps)*(math.log(26,2))
	return int(k*(math.log(k,2)))

# Return sorted list of starting indices where p matches x
def modPatternMatch(q,p,x):
	it = len(p)-1
	val = 1
	pattern = 0
	curr = 0
	while(it > -1):
		pattern = (pattern + (((ord(p[it])-65)%q)*val)%q)%q
		curr = (curr + (((ord(x[it])-65)%q)*val)%q)%q
		if(it == 0):
			break
		val = (val*26)%q
		it -= 1
	ans = []
	it = 0
	if(curr == pattern):
		ans.append(it)
	it += 1
	while(it < len(x)-len(p)+1):
		curr = ((((curr-(((ord(x[it-1])-65)%q)*val)%q)%q)*(26%q))%q + (ord(x[it+len(p)-1])-65)%q)%q
		if(curr < 0):
			curr += q
		if(curr == pattern):
			ans.append(it)
		it += 1
	print(len(ans))
	return ans

# Return sorted list of starting indices where p matches x
def modPatternMatchWildcard(q,p,x):
	wild_index = 0
	while(wild_index< len(p)):
		if(p[wild_index] == '?'):
			break
		wild_index += 1
	it = len(p) - 1
	val = 1
	pattern = 0
	curr = 0
	wild_val = 0
	while(it > -1):
		if(it == wild_index):
			wild_val = val
			val = (val*26)%q
			it -= 1
			continue
		pattern += (((ord(p[it])-65)%q)*val)%q
		pattern = (pattern)%q
		curr += (((ord(x[it])-65)%q)*val)%q
		curr = (curr)%q
		if(it == 0):
			break
		val = (val*26)%q
		it -= 1
	it = 0
	ans = []
	if(curr == pattern):
		ans.append(it)
	it += 1
	wild_index += 1
	while(it < len(x)-len(p)+1):
		curr = (curr-(((ord(x[it-1])-65)%q)*val)%q)%q
		curr = (curr*(26%q))%q
		curr = (curr+(ord(x[it+len(p)-1])-65)%q)%q
		curr = (curr + ((ord(x[wild_index-1])-65)%q)*((wild_val*(26%q))%q))%q
		curr = (curr - ((ord(x[wild_index])-65)%q)*((wild_val)%q))%q
		if(curr < 0):
			curr += q
		if(curr == pattern):
			ans.append(it)
		it += 1
		wild_index += 1
	return ans

